import { ADD_TO_CART, REMOVE_TO_CART, CLEAR_TO_CART} from '../constants'

const initialState = {
  cartData :[]
}

export default function cartItem(state = [], action){
   switch (action.type) {
       case ADD_TO_CART:
           const cartParameter = {"itemId":action.data.id,"userId":sessionStorage.getItem('userID')}
           console.log(cartParameter);
           fetch('http://localhost:6021/AddToCart',{ "params": cartParameter}).then(res => res.json());
           return [
               ...state,
               {cartData: action.data}
            ]
        case REMOVE_TO_CART:
            const removeParameter = {itemId:action.data,userId:sessionStorage.getItem('userID')}
            fetch('http://localhost:6021/RemoveToCart',{ params: removeParameter}).then(res => res.json());
            state.splice(action.data,1)
            return [
                ...state
            ]
        case CLEAR_TO_CART:
            state = [];
            return [
                ...state
            ]
       default:
           return state
          
   }
}
