import { ADD_TO_CART, REMOVE_TO_CART,CLEAR_TO_CART} from '../constants'
export const addToCart = (data) =>{
    closeModal()
  return{
      type:ADD_TO_CART,
      data:data
  }
}
export const removeToCart = (data) =>{
    return{
        type:REMOVE_TO_CART,
        data:data
    }
  }
  export const clearToCart = (data) =>{
    return{
        type:CLEAR_TO_CART
    }
  }
