import React, { useState } from "react";
import { useHistory } from 'react-router-dom';
import Modal from 'react-modal';
import HeaderContainer from "../containers/HeaderContainer";
import './Dashboard.css';
const customStyles = {
    content : {
      top                   : '30%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
};
Modal.setAppElement('#root')
function Dashboard(props){
    const [modalIsOpen,setIsOpen] = React.useState(false);
    function openModal() {
      setIsOpen(true);
    }
    function closeModal(){
      setIsOpen(false);
      props.clearToCartHandler();
      history.push("/Dashboard");
    }
    const username = sessionStorage.getItem("username");
    //const userID = sessionStorage.getItem("userID");
    const [msg,setMsg] = useState("");
    const [dataSetArr,setData] = useState();
  
    const getItems = () => fetch('http://localhost:6021/GetData').then(res => res.json());
    getItems().then(data => setData(data.data));
return(
       <>
           <HeaderContainer />
            <p className="NoData">{msg}</p> 
            <div className="container-fluid">
               <div className="row">
               {dataSetArr?dataSetArr.map(((item, index) => (
                   <div className="col-sm-3 text-center" key={index}>
                     <div className="prd_border">
                      <h4 className="title content">{item.itemName}</h4>
                      <p>{item.keyword}</p>
                      <p>Rs. {item.minPrice}</p>
                      <button type="button" className="btn btn-sm btn-default btn_css" onClick={ () => props.addToCartHandler(item)}>Add to Cart</button>
                     </div>
                   </div>
                   ))):""}
               </div>
            </div>
            <Modal isOpen={modalIsOpen} onRequestClose={closeModal} style={customStyles} contentLabel="Example Modal">
               <h6 className="title text-center modal_title"> Your Order Successfully Placed</h6>
               <div className="text-center">
                   <p className="title">Added in cart</p>
               </div> 
            </Modal>
       </>
   );
}
export default Dashboard;
