import React, { useState, useEffect } from "react";
import './Dashboard.css';
var FontAwesome = require('react-fontawesome');
function Dashboard(){
    const username = sessionStorage.getItem("username");
    //const userID = sessionStorage.getItem("userID");
    const [msg,setMsg] = useState("");
    const [dataSetArr,setData] = useState();
    const addToCart = (id) =>{
        alert(id);
    }
    const getItems = () => fetch('http://localhost:6021/GetData').then(res => res.json());
    useEffect(() => {
        getItems().then(data => setData(data.data));
    }, []);
  
    //console.log(dataSetArr);  
return(
       <>
            <nav className="navbar navbar-inverse">
                <div className="container-fluid">
                     <div className="navbar-header">
                         <a className="navbar-brand title">MedApp</a>
                     </div>
                    <ul className="nav navbar-nav navbar-right">
                       <li><a className="list" href="#"><i className="fa fa-shopping-cart" /> 0 </a></li>
                       <li><a className="list"><span className="glyphicon glyphicon-user"></span>Hi, {username}</a></li>
                    </ul>
                </div>
            </nav>
            <p className="NoData">{msg}</p> 
            <div className="container-fluid">
               <div className="row">
               {dataSetArr?dataSetArr.map(((item) => (
                   <div className="col-sm-3 text-center">
                     <div className="prd_border">
                      <h4 className="title content">{item.itemName}</h4>
                      <p>{item.keyword}</p>
                      <p>Rs. {item.minPrice}</p>
                      <button className="btn btn-sm btn-default btn_css" onClick={addToCart(item.id)}>Add Cart</button>
                     </div>
                   </div>
                   ))):""}
               </div>
            </div>
       </>
   );
}
export default Dashboard;
