import { ADD_TO_CART, REMOVE_TO_CART} from '../constants'

const initialState = {
  cartData :[]
}

export default function cartItem(state = [], action){
   switch (action.type) {
       case ADD_TO_CART:
           return [
               ...state,
               {cartData: action.data}
            ]
        case REMOVE_TO_CART:
            console.log(action.data);
            const pos = state.indexOf(action.data)
            console.log(pos);
            //state.splice(pos,1)
            //state.pop(action.data.cartData);
            return [
                ...state
            ]
       default:
           return state
          
   }
}
