import React, { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import './Dashboard.css';
function Dashboard(){
    const history = useHistory();
    const username = sessionStorage.getItem("username");
    //const userID = sessionStorage.getItem("userID");
    const cartData = JSON.parse(localStorage.getItem("cart"));
    const [msg,setMsg] = useState("");
    const [dataSetArr,setData] = useState();
    const [quantity,setQty] = useState(1);
    const [cart,setCart] = useState([]);
    const [CartLength,setCartLength] = useState(1);
    
    if(cartData.length > 0){
        setCartLength(cartData.length);
      }
      else{
        setCartLength(0)
      }
    const setQuantity = (event) =>{
       setQty(event.target.value);
       console.log(quantity);
    }
    const addToCart = (item) => {
        console.log("item added");
        setCart([...cart,item]);
        sessionStorage.setItem("Cart", JSON.stringify(cart));
        console.log(cart);
    }
    const navigateTo = () =>{
        history.push("/Cart");
    }
    const getItems = () => fetch('http://localhost:6021/GetData').then(res => res.json());
    useEffect(() => {
        getItems().then(data => setData(data.data));
    }, []);
  
    //console.log(dataSetArr);  
return(
       <>
            <nav className="navbar navbar-inverse">
                <div className="container-fluid">
                     <div className="navbar-header">
                         <a href="#" className="navbar-brand title">MedApp</a>
                     </div>
                    <ul className="nav navbar-nav navbar-right">
                       <li><a href="#" className="list" onClick={() =>navigateTo()}><i className="fa fa-shopping-cart" /> your cart ({CartLength}) </a></li>
                       <li><a className="list"><span className="glyphicon glyphicon-user"></span>Hi, {username}</a></li>
                    </ul>
                </div>
            </nav>
            <p className="NoData">{msg}</p> 
            <div className="container-fluid">
               <div className="row">
               {dataSetArr?dataSetArr.map(((item, index) => (
                   <div className="col-sm-3 text-center" key={index}>
                     <div className="prd_border">
                      <h4 className="title content">{item.itemName}</h4>
                      <p>{item.keyword}</p>
                      <p>Rs. {item.minPrice}</p>
                      <p>Test Qty : <input type="number" key={item.id} className="prd_qty" onChange={setQuantity}/> </p>
                      <button type="button" className="btn btn-sm btn-default btn_css" onClick={ () => addToCart(item)}>Add to Cart</button>
                     </div>
                   </div>
                   ))):""}
               </div>
            </div>
       </>
   );
}
export default Dashboard;
